mapper.select:
SELECT knowledge.id ,content ,title ,type_id ,kname ,publish_time ,summary ,key_word FROM knowledge LEFT JOIN knowledge_type ON knowledge.type_id = knowledge_type.id
type_id = #{typeId}
AND title LIKE #{page.finalQuery}
LIMIT ${page.start},${page.pageSize};

mapper.update:
UPDATE 
	knowledge_type 
SET 
	kname=#{kname}
	,description=#{description} 
WHERE 
	id=#{id}
