mapper.select:
SELECT id, vindex, vname, thumbnail_url, description, video_url, price FROM video
WHERE vname LIKE #{page.finalQuery}
ORDER BY id DESC LIMIT #{page.start},#{page.pageSize}

SELECT vb.*, v.`price`, v.`vname`, v.thumbnail_url, v.video_url, c.`nickname` FROM video_buy_record vb LEFT JOIN video v ON vb.video_id = v.id LEFT JOIN customer c ON vb.`customer_id` = c.`id`
vb.`status` = #{record.status}
AND submit_time < #{record.submitTime}
AND submit_time > #{record.coment}
AND v.vname LIKE #{name}
