mapper.select:
SELECT topic.id AS topic_id ,topic.has_video AS topic_has_video ,topic.video_url AS topic_video_url ,topic.title AS topic_title ,topic.content As topic_content ,topic.publish_time As topic_publish_time ,topic.status As topic_status ,customer.id AS customer_id ,customer.nickname AS customer_nickName ,customer.avatar_url AS customer_avatarUrl ,img.id AS img_id ,img.`img_url` AS img_url ,(SELECT COUNT(id) FROM topic_coment WHERE topic_coment.topic_id=topic.`id`) AS totalComment ,(SELECT COUNT(id) FROM topic_thumbsup WHERE topic_thumbsup.topic_id=topic.`id`) AS totalThumbsup
,(SELECT COUNT(id) FROM topic_thumbsup WHERE topic_thumbsup.topic_id = topic.id AND topic_thumbsup.customer_id =#{customerId})AS flag
,0 AS flag
FROM topic LEFT JOIN customer ON customer.`id`=topic.`customer_id` LEFT JOIN img ON img.`foreign_id`=topic.id AND img.`img_type`='1'
topic.type_id=#{topicTypeId}
ORDER BY topic.`id` DESC,img.`id` ASC LIMIT ${page.start},${page.pageSize}
